package com.naveen;

public class MathOperationsOfTwoNumbers {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int firstNumber=20,secondNumber=4;
        System.out.println("Addition of two Numbers:" +(firstNumber+secondNumber));
        System.out.println("Subraction of two  Numbers:"+(firstNumber-secondNumber));
        System.out.println("Multiplication of two  Numbers:"+(firstNumber*secondNumber));
        System.out.println("Divison of two  Numbers:"+(firstNumber/secondNumber));
        System.out.println("Remainder of two  Numbers:"+(firstNumber%secondNumber));
	}

}
